SELECT  FROM product


    AND weight = #{weight,jdbcType=INTEGER}


    AND product_name = #{productName,jdbcType=VARCHAR}


    AND unit_name = #{unitName,jdbcType=VARCHAR}


    AND bar_code = #{barCode,jdbcType=VARCHAR}


    AND remark = #{remark,jdbcType=VARCHAR}


    AND create_time = #{createTime,jdbcType=TIMESTAMP}


    AND update_time = #{updateTime,jdbcType=TIMESTAMP}


    AND type_id = #{typeId,jdbcType=SMALLINT}


    AND status = #{status,jdbcType=TINYINT}


    AND image = #{image,jdbcType=VARCHAR}


    AND arithmetic_type = #{arithmeticType,jdbcType=TINYINT}


    AND is_material = #{isMaterial,jdbcType=TINYINT}


    AND unit_size = #{unitSize,jdbcType=VARCHAR}


    AND sku_id = #{skuId,jdbcType=VARCHAR}


    AND update_user = #{updateUser,jdbcType=VARCHAR}


    AND is_scan = #{isScan,jdbcType=TINYINT}


    AND is_sale = #{isSale,jdbcType=TINYINT}


    AND is_inventory_scan = #{isInventoryScan,jdbcType=TINYINT}


    AND category = #{category,jdbcType=VARCHAR}


    AND category_path = #{categoryPath,jdbcType=VARCHAR}


    AND sizes = #{sizes,jdbcType=VARCHAR}


    AND weight_unit = #{weightUnit,jdbcType=VARCHAR}


    AND propertys = #{propertys,jdbcType=VARCHAR}


    AND storage_value_id = #{storageValueId,jdbcType=VARCHAR}


    AND purchase_on = #{purchaseOn,jdbcType=TINYINT}


    AND is_batch = #{isBatch,jdbcType=TINYINT}


    AND is_batch_time = #{isBatchTime,jdbcType=TIMESTAMP}


    AND transfer_type = #{transferType,jdbcType=TINYINT}


    AND purchase_strategy = #{purchaseStrategy,jdbcType=TINYINT}


    AND box_size = #{boxSize,jdbcType=VARCHAR}


    AND labor_cost = #{laborCost,jdbcType=VARCHAR}


    AND in_tax = #{inTax,jdbcType=DECIMAL}


    AND out_tax = #{outTax,jdbcType=DECIMAL}


    AND only_general_store = #{onlyGeneralStore,jdbcType=TINYINT}


    AND is_auto = #{isAuto,jdbcType=TINYINT}


    AND transfer_advance_day = #{transferAdvanceDay,jdbcType=SMALLINT}


    AND purchase_to_address = #{purchaseToAddress,jdbcType=TINYINT}


    AND store_inventory = #{storeInventory,jdbcType=TINYINT}


    AND store_list = #{storeList,jdbcType=VARCHAR}


    AND auto_batch = #{autoBatch,jdbcType=TINYINT}


    AND weight_limit = #{weightLimit,jdbcType=VARCHAR}


    AND producer = #{producer,jdbcType=TINYINT}


    AND quality_period = #{qualityPeriod,jdbcType=INTEGER}


    AND sale_period = #{salePeriod,jdbcType=INTEGER}


    AND batch_type = #{batchType,jdbcType=TINYINT}


    AND distribute_cost = #{distributeCost,jdbcType=VARCHAR}


    AND basket_num = #{basketNum,jdbcType=LONGVARCHAR}

  LIMIT 1


