mapper.select:

SELECT  FROM store

  
    AND store_name = #{storeName,jdbcType=VARCHAR}
  
  
    AND city_id = #{cityId,jdbcType=VARCHAR}
  
  
    AND address = #{address,jdbcType=VARCHAR}
  
  
    AND phone = #{phone,jdbcType=VARCHAR}
  
  
    AND create_time = #{createTime,jdbcType=TIMESTAMP}
  
  
    AND update_time = #{updateTime,jdbcType=TIMESTAMP}
  
  
    AND status = #{status,jdbcType=TINYINT}
  
  
    AND is_purchase = #{isPurchase,jdbcType=TINYINT}
  
  
    AND is_general = #{isGeneral,jdbcType=TINYINT}
  
  
    AND update_user = #{updateUser,jdbcType=VARCHAR}
  
  
    AND station_id = #{stationId,jdbcType=VARCHAR}
  
  
    AND sort = #{sort,jdbcType=REAL}
  
  
    AND is_test = #{isTest,jdbcType=TINYINT}
  
  
    AND is_prestore = #{isPrestore,jdbcType=TINYINT}
  
  
    AND is_sale = #{isSale,jdbcType=TINYINT}
  
  
    AND is_scan = #{isScan,jdbcType=TINYINT}
  
  
    AND type = #{type,jdbcType=TINYINT}
  
  
    AND is_chive_notice = #{isChiveNotice,jdbcType=BIT}
  
  
    AND remark = #{remark,jdbcType=LONGVARCHAR}
  
  LIMIT 1


