    AND status = #{status,jdbcType=TINYINT}


    AND biz_id = #{bizId,jdbcType=INTEGER}


    AND finance_biz_id = #{financeBizId,jdbcType=VARCHAR}


    AND finance_biz_type = #{financeBizType,jdbcType=TINYINT}


    AND finance_biz_subtype = #{financeBizSubtype,jdbcType=TINYINT}


    AND finance_biz_status = #{financeBizStatus,jdbcType=TINYINT}


    AND finance_biz_amount = #{financeBizAmount,jdbcType=DECIMAL}


    AND finance_biz_finish_time = #{financeBizFinishTime,jdbcType=TIMESTAMP}


    AND vendor_id = #{vendorId,jdbcType=INTEGER}


    AND vendor_name = #{vendorName,jdbcType=VARCHAR}


    AND vendor_status = #{vendorStatus,jdbcType=TINYINT}


    AND vendor_resp_groups = #{vendorRespGroups,jdbcType=VARCHAR}


    AND op_status = #{opStatus,jdbcType=TINYINT}


    AND jindie_amount = #{jindieAmount,jdbcType=DECIMAL}


    AND balance = #{balance,jdbcType=DECIMAL}


    AND ddmc_amount = #{ddmcAmount,jdbcType=DECIMAL}


    AND biz_id_subtype = #{bizIdSubtype,jdbcType=INTEGER}


    AND sync = #{sync,jdbcType=BIGINT}


    AND create_time = #{createTime,jdbcType=TIMESTAMP}


    AND update_time = #{updateTime,jdbcType=TIMESTAMP}

  LIMIT 1


