SELECT  FROM pay_record


    AND vendor_id = #{vendorId,jdbcType=INTEGER}


    AND finish_time = #{finishTime,jdbcType=TIMESTAMP}


    AND submit_time = #{submitTime,jdbcType=TIMESTAMP}


    AND use_to = #{useTo,jdbcType=VARCHAR}


    AND remark = #{remark,jdbcType=VARCHAR}


    AND plan_total_amount = #{planTotalAmount,jdbcType=DECIMAL}


    AND real_total_amount = #{realTotalAmount,jdbcType=DECIMAL}


    AND create_time = #{createTime,jdbcType=TIMESTAMP}


    AND update_time = #{updateTime,jdbcType=TIMESTAMP}


    AND create_user = #{createUser,jdbcType=VARCHAR}


    AND update_user = #{updateUser,jdbcType=VARCHAR}


    AND finance_update_user = #{financeUpdateUser,jdbcType=VARCHAR}


    AND status = #{status,jdbcType=TINYINT}


    AND submit_user = #{submitUser,jdbcType=VARCHAR}


    AND last_pay_day = #{lastPayDay,jdbcType=VARCHAR}


    AND approver = #{approver,jdbcType=VARCHAR}


    AND invoice_option = #{invoiceOption,jdbcType=TINYINT}


    AND earliest_pay_day = #{earliestPayDay,jdbcType=VARCHAR}


    AND pay_type = #{payType,jdbcType=TINYINT}


    AND settlement_amount = #{settlementAmount,jdbcType=DECIMAL}


    AND prepay_amount = #{prepayAmount,jdbcType=DECIMAL}


    AND pay_bank_id = #{payBankId,jdbcType=INTEGER}


    AND details = #{details,jdbcType=LONGVARCHAR}


    AND vendor_info = #{vendorInfo,jdbcType=LONGVARCHAR}

  LIMIT 1


