SELECT  FROM purchase


    AND vendor_id = #{vendorId,jdbcType=INTEGER}


    AND store_id = #{storeId,jdbcType=INTEGER}


    AND create_time = #{createTime,jdbcType=TIMESTAMP}


    AND delivery_time = #{deliveryTime,jdbcType=TIMESTAMP}


    AND purchase_title = #{purchaseTitle,jdbcType=VARCHAR}


    AND status = #{status,jdbcType=TINYINT}


    AND draft_time = #{draftTime,jdbcType=TIMESTAMP}


    AND amount_total = #{amountTotal,jdbcType=DECIMAL}


    AND real_amount_total = #{realAmountTotal,jdbcType=DECIMAL}


    AND creater = #{creater,jdbcType=VARCHAR}


    AND update_time = #{updateTime,jdbcType=TIMESTAMP}


    AND finance_status = #{financeStatus,jdbcType=TINYINT}


    AND finance_remark = #{financeRemark,jdbcType=VARCHAR}


    AND finance_time = #{financeTime,jdbcType=TIMESTAMP}


    AND finance_operator = #{financeOperator,jdbcType=VARCHAR}


    AND plan_id = #{planId,jdbcType=INTEGER}


    AND update_user = #{updateUser,jdbcType=VARCHAR}


    AND is_timeout = #{isTimeout,jdbcType=TINYINT}


    AND type_ids = #{typeIds,jdbcType=VARCHAR}


    AND settlement_amount_total = #{settlementAmountTotal,jdbcType=DECIMAL}


    AND communicate_time = #{communicateTime,jdbcType=TINYINT}


    AND temperature = #{temperature,jdbcType=VARCHAR}


    AND car_environment = #{carEnvironment,jdbcType=TINYINT}


    AND quality_control_status = #{qualityControlStatus,jdbcType=TINYINT}


    AND additional_total = #{additionalTotal,jdbcType=DECIMAL}


    AND is_arrival = #{isArrival,jdbcType=TINYINT}


    AND quality_time = #{qualityTime,jdbcType=TIMESTAMP}


    AND receive_operator = #{receiveOperator,jdbcType=VARCHAR}


    AND plan_delivery_time = #{planDeliveryTime,jdbcType=TIMESTAMP}


    AND is_replenishment = #{isReplenishment,jdbcType=TINYINT}


    AND is_weigh_completed = #{isWeighCompleted,jdbcType=TINYINT}


    AND is_prepay = #{isPrepay,jdbcType=TINYINT}


    AND adjustment_amount = #{adjustmentAmount,jdbcType=DECIMAL}


    AND final_settlement_amount = #{finalSettlementAmount,jdbcType=DECIMAL}


    AND finish_time = #{finishTime,jdbcType=TIMESTAMP}


    AND products = #{products,jdbcType=LONGVARCHAR}


    AND import_log = #{importLog,jdbcType=LONGVARCHAR}


    AND images = #{images,jdbcType=LONGVARCHAR}


    AND supplier_sign = #{supplierSign,jdbcType=LONGVARCHAR}


    AND additional = #{additional,jdbcType=LONGVARCHAR}


    AND adjustment_amount_detail = #{adjustmentAmountDetail,jdbcType=LONGVARCHAR}

  LIMIT 1


