SELECT  FROM vendor


    AND vendor_name = #{vendorName,jdbcType=VARCHAR}


    AND address = #{address,jdbcType=VARCHAR}


    AND phone = #{phone,jdbcType=VARCHAR}


    AND create_time = #{createTime,jdbcType=TIMESTAMP}


    AND update_time = #{updateTime,jdbcType=TIMESTAMP}


    AND status = #{status,jdbcType=TINYINT}


    AND replenish_rate = #{replenishRate,jdbcType=DECIMAL}


    AND type = #{type,jdbcType=TINYINT}


    AND update_user = #{updateUser,jdbcType=VARCHAR}


    AND bank_name = #{bankName,jdbcType=VARCHAR}


    AND bank_account = #{bankAccount,jdbcType=VARCHAR}


    AND account_name = #{accountName,jdbcType=VARCHAR}


    AND is_account_active = #{isAccountActive,jdbcType=TINYINT}


    AND tax_account = #{taxAccount,jdbcType=VARCHAR}


    AND account_rate = #{accountRate,jdbcType=DECIMAL}


    AND vendor_type = #{vendorType,jdbcType=TINYINT}


    AND contact_user = #{contactUser,jdbcType=VARCHAR}


    AND audit_status = #{auditStatus,jdbcType=TINYINT}


    AND audit_update_time = #{auditUpdateTime,jdbcType=TIMESTAMP}


    AND audit_update_user = #{auditUpdateUser,jdbcType=VARCHAR}


    AND audit_remark = #{auditRemark,jdbcType=VARCHAR}


    AND account_period_type = #{accountPeriodType,jdbcType=TINYINT}


    AND is_replenishment = #{isReplenishment,jdbcType=TINYINT}


    AND min_order_amount = #{minOrderAmount,jdbcType=DECIMAL}


    AND is_store_purchase = #{isStorePurchase,jdbcType=TINYINT}


    AND delivery_day = #{deliveryDay,jdbcType=SMALLINT}


    AND username = #{username,jdbcType=VARCHAR}


    AND password = #{password,jdbcType=VARCHAR}


    AND is_quality = #{isQuality,jdbcType=TINYINT}


    AND delivery_password = #{deliveryPassword,jdbcType=VARCHAR}


    AND city_id = #{cityId,jdbcType=VARCHAR}


    AND short_name = #{shortName,jdbcType=VARCHAR}


    AND is_self_purchase = #{isSelfPurchase,jdbcType=TINYINT}


    AND arrival_deadline = #{arrivalDeadline,jdbcType=VARCHAR}


    AND need_vms = #{needVms,jdbcType=BIT}


    AND supply_product_type = #{supplyProductType,jdbcType=VARCHAR}


    AND remark = #{remark,jdbcType=LONGVARCHAR}


    AND images = #{images,jdbcType=LONGVARCHAR}


    AND rest_time = #{restTime,jdbcType=LONGVARCHAR}


    AND intro_images = #{introImages,jdbcType=LONGVARCHAR}


    AND introduce = #{introduce,jdbcType=LONGVARCHAR}

  LIMIT 1


